import requests
import base64
import json
import cv2
import numpy as np
import chardet


def draw(image_path, response):
    image = cv2.imread(image_path)
    for tabel in response['tableRes']['tables']:
        for cell in tabel['cells']:
            points = cell['cell_coord_point']
            points = np.array([[i['x'], i['y']] for i in points])
            if cell['tl_col'] == -1:
                cv2.polylines(image, [points], isClosed=True, color=(255, 0, 0), thickness=2)
            else:
                cv2.polylines(image, [points], isClosed=True, color=(0, 0, 255), thickness=2)
            print(cell['cell_content_text'])  # .decode('gbk', 'ignore')

        if tabel['type'] != 0:
            x_set = [i['x'] for i in tabel['table_coord_point']]
            y_set = [i['y'] for i in tabel['table_coord_point']]
            cv2.rectangle(image, (min(x_set), min(y_set)), (max(x_set), max(y_set)), (0, 125, 125), 5)
    cv2.imshow('image', image)
    cv2.waitKey()


def do_img(img_path):
    # img = base64.b64encode(open(img_path, 'rb').read()).decode('utf8')  # 直接读取二进制
    img = base64.b64encode(cv2.imencode(".jpg", cv2.imread(img_path))[1].tobytes()).decode('utf8')  # cv2 mat格式转二进制
    request_url = "http://106.55.81.17:60007/youtu/ocrapi/tableocr"
    params = {"app_id": '123456', "image": img,
              "options": {"preprocess": True, 
                          "enable_ret_excel": True, 
                          "side_detect_short": 20,
                          "side_detect_long": 3500}}
    #     params={
    #     "app_id":"123",
    #     "image":"",
    #     "options": {
    #         "preprocess":False,
    #         "enable_location":True,
    #         "enable_ret_excel":True,
    #         "enable_debug_info":True
    #     }
    # }
    headers = {'Host': '<calculated when request is sent>',
               'Content-type': 'application/json',
               'Content-Length': '<calculated when request is sent>'}
    response = requests.post(request_url, data=json.dumps(params), headers=headers)
    response.encoding = 'utf-8'
    response = json.loads(response.text)

    draw(img_path, response)
    print(response)


